import pytest
from typing import Any, Dict
from httpx import AsyncClient


class TestMedia:
    default_media: Dict[str, Any] = {}

    @pytest.mark.asyncio(scope="session")
    @pytest.mark.dependency(name="create_media")
    async def test_create_media(self, client: AsyncClient):
        response = await client.post(
            "/media/",
            json={
                "media_name": "property_image",
                "media_type": "png",
                "is_thumbnail": True,
                "content_url": "data:image/png;base64,",
            },
        )
        assert response.status_code == 200

        TestMedia.default_media = response.json()["data"]

    @pytest.mark.asyncio(scope="session")
    async def test_get_all_media(self, client: AsyncClient):
        response = await client.get("/media/", params={"limit": 10, "offset": 0})
        assert response.status_code == 200
        assert isinstance(response.json(), dict)

    @pytest.mark.asyncio(scope="session")
    @pytest.mark.dependency(depends=["create_media"], name="get_media_by_id")
    async def test_get_media_by_id(self, client: AsyncClient):
        media_id = self.default_media["media_id"]

        response = await client.get(f"/media/{media_id}")

        assert response.status_code == 200
        assert response.json()["data"]["media_id"] == media_id

    @pytest.mark.asyncio(scope="session")
    @pytest.mark.dependency(depends=["get_media_by_id"], name="update_media_by_id")
    async def test_update_media(self, client: AsyncClient):
        media_id = self.default_media["media_id"]

        response = await client.put(
            f"/media/{media_id}",
            json={
                "media_id": media_id,
                "media_name": "updated_property_image",
                "media_type": "png",
                "is_thumbnail": False,
                "content_url": "data:image/png;base64,",
            },
        )
        assert response.status_code == 200
        assert response.json()["data"]["media_name"] == "updated_property_image"

    @pytest.mark.asyncio(scope="session")
    @pytest.mark.dependency(depends=["update_media_by_id"], name="delete_media_by_id")
    async def test_delete_media(self, client: AsyncClient):
        media_id = self.default_media["media_id"]

        response = await client.delete(f"/media/{media_id}")
        assert response.status_code == 204

        # Verify the media is deleted
        response = await client.get(f"/media/{media_id}")
        assert response.status_code == 404
